import { Component, OnInit } from '@angular/core';
import { environment } from '../../../environments/environment';
import { Country } from '../../../app/interfaces/Country';
import { League } from '../../../app/interfaces/League';
import axios from 'axios';

@Component({
  selector: 'app-dash',
  templateUrl: './dash.component.html',
  styleUrls: ['./dash.component.css']
})
export class DashComponent implements OnInit {
  responseData: any;
  selectedOption: string;
  endpointData: any;
  tableData: any[] = [];




  constructor() {
    this.selectedOption = '';

  }

  ngOnInit() {
    this.makeHttpRequest();
  }

  async makeHttpRequest() {
    let endpoint = '';

    switch (this.selectedOption) {
      case 'timezone':
        endpoint = `${environment.apiFootbal.url2}/timezone`;
        break;
      case 'countries':
        endpoint = `${environment.apiFootbal.url2}/countries`;
        break;
      case 'leagues':
        endpoint = `${environment.apiFootbal.url2}/leagues`;
        break;
      case 'teams':
        endpoint = `${environment.apiFootbal.url2}/teams`;
        break;
      default:
        // Caso nenhuma opção seja selecionada, não fazer a solicitação HTTP
        return;
    }

    const options = {
      method: 'GET',
      url: endpoint,
      headers: {

        'X-RapidAPI-Key': environment.apiFootbal.tokenKey,
        'X-RapidAPI-Host': environment.apiFootbal.tokenHost
      }
    };

    try {
      const response = await axios.request(options);
      this.responseData = response.data;
      console.log(response.data);

      switch (this.selectedOption) {
        case 'countries':
          this.tableData = this.responseData.data as Country[];
          break;
        case 'leagues':
          this.tableData = this.responseData.data as League[];
          break;
        default:
          this.tableData = [];
          break;
      }

    } catch (error) {
      console.error(error);
    }
    console.log(options)
  }

  onOptionSelected(option: string) {
    this.selectedOption = option;
    this.makeHttpRequest(); // Chama a função para fazer a solicitação HTTP quando a opção é selecionada
  }
}
