import { Component, OnInit } from '@angular/core';
import { environment } from '../../../environments/environment';
import axios from 'axios';

@Component({
  selector: 'app-dash',
  templateUrl: './dash.component.html',
  styleUrls: ['./dash.component.css']
})
export class DashComponent implements OnInit {
  responseData: any;
  selectedOption: string;
  endpointData: any;

  constructor() {
    this.selectedOption = '';
  }

  ngOnInit() {
    this.makeHttpRequest();
  }

  async makeHttpRequest() {
    let endpoint = '';

    switch (this.selectedOption) {
      case 'timezone':
        endpoint = `${environment.apiFootbal.url}/timezone`;
        break;
      case 'countries':
        endpoint = `${environment.apiFootbal.url}/countries`;
        break;
      case 'leagues':
        endpoint = `${environment.apiFootbal.url}/leagues`;
        break;
      case 'teams':
        endpoint = `${environment.apiFootbal.url}/teams`;
        break;
      default:
        // Caso nenhuma opção seja selecionada, não fazer a solicitação HTTP
        return;
    }

    const options = {
      method: 'GET',
      url: endpoint,
      headers: {
        'X-RapidAPI-Key': environment.apiFootbal.tokenKey,
        'X-RapidAPI-Host': environment.apiFootbal.tokenHost
      }
    };

    try {
      const response = await axios.request(options);
      this.responseData = response.data;
      console.log(response.data);

      // Define os dados do endpoint selecionado
      this.endpointData = this.responseData.data; // Assumindo que a resposta do servidor contém um campo "data" com os dados do endpoint
    } catch (error) {
      console.error(error);
    }
  }

  onOptionSelected(option: string) {
    this.selectedOption = option;
    this.makeHttpRequest(); // Chama a função para fazer a solicitação HTTP quando a opção é selecionada
  }
}
