import { Component, OnInit } from '@angular/core';
import { environment } from '../../../environments/environment';
import axios from 'axios';

@Component({
  selector: 'app-dash',
  templateUrl: './dash.component.html',
  styleUrls: ['./dash.component.css']
})
export class DashComponent implements OnInit {
  responseData: any;


  ngOnInit() {
    this.makeHttpRequest();
  }

  async makeHttpRequest() {
    const options = {
      method: 'GET',
      url: `${environment.apiFootbal.url}/${'leagues'}`,
      headers: {
        'X-RapidAPI-Key': environment.apiFootbal.tokenKey,
        'X-RapidAPI-Host': environment.apiFootbal.tokenHost
      }
    };

    try {
      const response = await axios.request(options);
      this.responseData = response.data; // Armazena os dados da resposta na propriedade
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  }
}

<div>
    <h1>Dados da resposta HTTP:</h1>
    <pre>{{ responseData | json }}</pre>
  </div>
